  constructor(private http: HttpClient) {
    console.log('Hello RestRouteProvider Provider');
  }

  public getLanguage():string{
    return this.pageLanguage.getValue();
  }

  public setLanguage(language:string){
    this.pageLanguage.next(language);
  }
  
  public setRoute(page : number, tour : number){
    console.log(this.getRoutePageNumber());
    if (this.getRoutePageNumber()!=page){
      this.routePage.next(page);
    }
    
    console.log(this.getRouteNumber());
    if (this.getRouteNumber()!=tour){
      this.routeSource.next(tour);
    }
  }
  
  public getRoute() {
    return this.rawRoute[this.routeSource.getValue()];
    //return this.rawRoute[this.route];
  }

  public getRouteNumber() : number {
    return this.routeSource.getValue();
    //return this.route;
  }

  public getRoutePageNumber() : number {
    return this.routePage.getValue();
    //return this.route;
  }

  public getAllRoutes(){
    return this.rawRoute;
  }

  public getAllTimeRoutes(){
    return this.rawRouteTime;
  }

  public getAllSounds(){
    return this.rawSounds;
  }

  public getJSONsubs(name:string): Observable<any> {
    return this.http.get("./assets/subs/"+name+".json")
  }
